import React, { useContext } from "react";
import { ScCartItem, ScCartItemDetails } from "./scParts";
import CartContext from "../contexts/CartContext";
import ProductContext from "../contexts/ProductContext";

const Item = (props) => {

  const { cart, setCart } = useContext(CartContext);
  const { localStorageWrite } = useContext(ProductContext);
  console.log(cart)

  const removeItem = () => {
    const updatedSepet = cart.filter((cartItem) => cartItem.id !== props.item.id);
    localStorageWrite(updatedSepet);
    setCart(updatedSepet);
  };

  return (
    <ScCartItem>
      <img src={props.item.image} alt={`${props.item.title} book`} />

      <ScCartItemDetails>
        <h2>{props.item.title}</h2>
        <p>$ {props.item.price}</p>
        <button onClick={() => removeItem(props.item)}>Remove from cart</button>
      </ScCartItemDetails>
    </ScCartItem>
  );
};

export default Item;
